ScriptGroup.script:
Planeswalker = Planeswalker or {}
Planeswalker.interfaceTable = Planeswalker.interfaceTable or {}
Planeswalker.interfaceTable.Limbs = Planeswalker.interfaceTable.Limbs or {}
Planeswalker.PW1Version = "0.1"

Script.script:
function pw1EventHandler(eventName)
	if eventName == "loggedIn" then
		Planeswalker.interfaceTable = {}
		Planeswalker.interfaceTable.Limbs = {}
		send("interface")
	end
end
